import React from "react";
import Imagem from "./Imagem";

class Pokemons2 extends React.Component {
    render() {
      const { name, type, averageWeight, image } = this.props.infos
        return (
            <div>
                  <div>
                <p>NOME: {name}</p>
                <Imagem imagem={image} textoAlterna='pokemons'/>
                <p>TIPO: {type}</p>
                <p>PESO: {averageWeight.value}{averageWeight.measurementUnit}</p>
            </div>
            </div>

        )
    }
}

export default Pokemons2;
