import React from "react";
import pokemons from "./data";
import Pokemons2 from "./pokemons2";
import Button from "./button";

class App extends React.Component {
	constructor() {
		super();
		this.state = {
			tipo: "",
		};


		this.dragon = this.dragon.bind(this);
	}


	dragon() {
		this.setState({ tipo: "Dragon" });
	}

	render() {
		const { tipo } = this.state;
		const final = pokemons.filter((poke) =>
			poke.type.includes(this.state.tipo),
		);

		return (
			<div>
				<div className="teste2">
					{final.map((teste) => (
						<Pokemons2 infos={teste} key={teste.id} />
					))}
				</div>
				<div>
					<h1>TIPO: {tipo}</h1>
					<Button func={this.dragon} />
				</div>
			</div>
		);
	}
}
export default App;
