import React from "react";
import pokemons from "./data";
import Pokemons2 from "./pokemons2";

class App extends React.Component {
	constructor() {
		super();
    this.state = {
      tipo: ''
    }
    this.eletric = this.teste.bind(this);

	}



	render() {
    const final = pokemons;
    const { tipo } = this.tipe

		return (
			<div>
				<div className="teste2">
					{final
						.filter((poke) => poke.type === { tipo })
						.map((teste) => (
							<Pokemons2 infos={teste} key={teste.id} />
						))}
				</div>
				<div>
          <button>Electric</button>
          <button>Fire</button>
          <button>Bug</button>
          <button>Poison'</button>
          <button>Psychic</button>
          <button>Normal</button>
          <button>Dragon</button> 
				</div>
			</div>
		);
	}
}
export default App;
