import React from "react";
import Imagem from "./Imagem";

class Pokemons2 extends React.Component {
	render() {
		const { name, averageWeight, image } = this.props.infos;
		return (
			<div className="geral">
                <div className="teste">
                <p>NOME: {name}</p>
					<Imagem imagem={image} textoAlterna="pokemons" />
					<p>TIPO: {this.props.type}</p>
					<p>
						PESO: {averageWeight.value}
						{averageWeight.measurementUnit}
					</p>
                </div>

			</div>
		);
	}
}

export default Pokemons2;
