import React from "react";
import pokemons from "./data";
import Pokemons2 from "./pokemons2";

class App extends React.Component {
	constructor() {
		super();
		this.state = {
			tipo: "",
		};

		this.eletric = this.eletric.bind(this);
		this.fire = this.fire.bind(this);
		this.bug = this.bug.bind(this);
		this.poison = this.poison.bind(this);
		this.psychic = this.psychic.bind(this);
		this.normal = this.normal.bind(this);
		this.dragon = this.dragon.bind(this);
		this.teste = this.teste.bind(this);
	}

	render() {
		const { tipo } = this.state;
		const final = pokemons.filter((poke) => poke.type.includes("Eletric"));

		return (
			<div>
				<div className="teste2">
					{final.map((teste) => (
						<Pokemons2 infos={teste} key={teste.id} />
					))}
				</div>
				<div>
					<h1>TIPO: {tipo}</h1>
					<button>Electric</button>
					<button>Fire</button>
					<button>Bug</button>
					<button>Poison</button>
					<button>Psychic</button>
					<button>Normal</button>
					<button>Dragon</button>
				</div>
			</div>
		);
	}
}
export default App;
