import React from "react";
import pokemons from "./data";
import Pokemons2 from "./pokemons2";

class App extends React.Component {
	constructor() {
		super();
		this.state = {
			tipo: "",
		};

		this.filtro = this.filtro.bind(this);

	}

filtro(nome) {


      this.setState({tipo: nome })

}

	render() {

    const { tipo } = this.state;
	  const final = pokemons.filter((poke) => poke.type.includes(this.state.tipo))


		return (
			<div>
				<div className="teste2">
					{final
						.map((teste) => (
							<Pokemons2 infos={teste} key={teste.id} />
						))}
				</div>
        <div>
          <h1>TIPO: {tipo }</h1>
					<button onClick={this.filtro('Eletric')}>Electric</button>
					<button onClick={this.filtro('Fire')}>Fire</button>
					<button onClick={this.filtro('Bug')}>Bug</button>
					<button onClick={this.filtro('Poison')}>Poison</button>
					<button onClick={this.filtro('Psychic')}>Psychic</button>
					<button onClick={this.filtro('Normal')}>Normal</button>
					<button onClick={this.filtro('Dragon')}>Dragon</button>
				</div>
			</div>
		);
	}
}
export default App;
