import React from "react";
import Imagem from "./Imagem";

class Pokemons2 extends React.Component {
	render() {
		const { name, averageWeight, type, image, moreInfo } = this.props.infos;
		return (
			//  className = 'Geral'
			<div className="subgeral">
				<Imagem imagem={image} textoAlterna="pokemons" />
				<div className="infos">
					<p>{name}</p>
					<div className="type-weight">
						<p>{type}</p>
						<p>
							{averageWeight.value}
							{averageWeight.measurementUnit}
						</p>
					</div>
					<div className="more-info">
						<a href={moreInfo}>MAIS INFORMAÇÕES</a>
					</div>
				</div>
			</div>
		);
	}
}

export default Pokemons2;
