import Imagem from "./Imagem";

class Pokemons2 extends React.Component {
	render() {
		const { name, averageWeight, type, image } = this.props.infos;
		return (
			<div className="geral">
                <div className="teste">
                <p>NOME: {name}</p>
					<Imagem imagem={image} textoAlterna="pokemons" />
					<p>TIPO: {type}</p>
					<p>
						PESO: {averageWeight.value}
						{averageWeight.measurementUnit}
					</p>
					<a href={ link }>MAIS INFORMAÇÕES</a>
                </div>

			</div>
		);
	}
}

