import React from "react";
import pokemons from "./data";
import Pokemons2 from "./pokemons2";

class App extends React.Component {
	constructor() {
		super();
		this.state = {
			tipo: "",
		};

		this.filtro = this.filtro.bind(this)
	}


	filtro(nome) {
				this.setState({ tipo: nome})
			}


	render() {


			  const final = pokemons.filter((poke) => poke.type.includes(this.state.tipo))



		return (
			<div>
				<div className="teste2">
				<div className="teste2">
					{final
						.map((teste) => (
							<Pokemons2 infos={teste} key={teste.id} />
						))}
				</div>
				</div>
				<div>
				<button onClick={this.teste('Eletric')}>Electric</button>
// 					<button onClick={this.fire}>Fire</button>
