import React from "react";
import pokemons from "./data";
import Pokemons2 from "./pokemons2";

class App extends React.Component {


	eletric() {
		this.setState((estado) => ({ tipo: (estado.tipo = "Eletric") }));
	}

	fire() {
		this.setState((estado) => ({ tipo: (estado.tipo = "Fire") }));
	}

	bug() {
		this.setState((estado) => ({ tipo: (estado.tipo = "Bug") }));
	}

	poison() {
		this.setState((estado) => ({ tipo: (estado.tipo = "Poison") }));
	}

	psychic() {
		this.setState((estado) => ({ tipo: (estado.tipo = "Psychic") }));
	}

	normal() {
		this.setState((estado) => ({ tipo: (estado.tipo = "Normal") }));
	}

	dragon() {
		this.setState((estado) => ({ tipo: (estado.tipo = "Dragon") }));
	}

	render() {
		const final = pokemons;


		return (
			<div>
				<div className="teste2">
					{final
						.filter((poke) => poke.type === 'Dragon')
						.map((teste) => (
							<Pokemons2 infos={teste} key={teste.id} />
						))}
				</div>
				<div>
					<button onClick={this.eletric}>Electric</button>
					<button onClick={this.fire}>Fire</button>
					<button onClick={this.bug}>Bug</button>
					<button onClick={this.poison}>Poison'</button>
					<button onClick={this.psychic}>Psychic</button>
					<button onClick={this.normal}>Normal</button>
					<button onClick={this.dragon}>Dragon</button>
				</div>
			</div>
		);
	}
}
export default App;
